import React, { Component, useState } from 'react';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowDropUpIcon from '@material-ui/icons/ArrowDropUp';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { Grid, Paper, Slider, Typography,} from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    paper: {
        verticalAlign: 'middle',
        '& > *': {
            margin: theme.spacing(2),
            width: '100%',
            /* height: theme.spacing(16), */
        },
        backgroundColor: '#f2f2f2',
    },
    grid: {
        padding: theme.spacing(1),
    }
}));

const mockCodes = [
    { code: 'KPNL145' },
    { code: 'KNUGOVNO' },
    { code: '145LOVE145' },
];

const mockCities = [
    { code: 'Rubezhnoe' },
    { code: 'Paris' },
    { code: 'London' },
];



function valueLabelFormat(value) {
    if (value === 100) {
        return '23:59'
    }

    let hours = Math.floor(24*value/100);
    let hourBegin = 100 * hours / 12;
    let minutesDecider = value - hourBegin;
    let minutes = Math.floor(60*minutesDecider/(100/24));

    return `${hours}:${minutes}`;
}



function BrowseFlights() {
    const classes = useStyles();
    const [showFilters, setShowFilters] = useState(true);

    //TODO: fetch codes, cities

    return (<div>
        <div style={{}}>
            Filters
            <IconButton
                color="primary"
                aria-label="show filters"
                size="small"
                onClick={() => setShowFilters(!showFilters)}
            >
                {showFilters ? <ArrowDropUpIcon /> : <ArrowDropDownIcon />}
            </IconButton>
        </div>


        {!showFilters ? null :
            <Paper elevation={3} className={classes.paper}>
                <Grid container spacing={5} alignItems="center" className={classes.grid}>
                    <Grid item xs={2}>
                        <Typography>Flight Code:</Typography>
                    </Grid>
                    <Grid item xs={10}>
                        <Autocomplete
                            className={classes.fileds}
                            size="small"
                            id="combo-box-code"
                            options={mockCodes}
                            getOptionLabel={(option) => option.code}
                            style={{ width: 300 }}
                            renderInput={(params) => <TextField {...params} placeholder="KPNL145" variant="outlined" />}
                        />
                    </Grid>

                    <Grid item xs={2}>
                        <Typography>Destination:</Typography>
                    </Grid>
                    <Grid item xs={10}>
                        <Autocomplete
                            className={classes.fileds}
                            size="small"
                            id="combo-box-destination"
                            options={mockCities}
                            getOptionLabel={(option) => option.code}
                            style={{ width: 300 }}
                            renderInput={(params) => <TextField {...params} placeholder="Rubezhnoe" variant="outlined" />}
                        />
                    </Grid>

                    <Grid item xs={2}>
                        <Typography>Date:</Typography>
                    </Grid>
                    <Grid item xs={10}>
                        <TextField
                            id="date"
                            type="date"
                            variant='outlined'
                            size="small"
                            style={{ width: 300 }}
                            InputLabelProps={{
                                shrink: true,
                            }}
                        />
                    </Grid>

                    <Grid item xs={2}>
                        <Typography>Time:</Typography>
                    </Grid>
                    <Grid item xs={10}>
                        <Slider
                            valueLabelFormat={valueLabelFormat}
                            defaultValue={[0, 100]}
                            style={{ width: 300 }}
                            valueLabelDisplay="auto"
                            aria-labelledby="non-linear-slider"
                        />
                    </Grid>

                </Grid>
            </Paper>
        }

    </div>);

}

export default BrowseFlights;
