import React, { Component, useState } from 'react';
import ArrowDropDownIcon from '@material-ui/icons/ArrowDropDown';
import ArrowDropUpIcon from '@material-ui/icons/ArrowDropUp';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import { Grid, Paper } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    paper: {
        verticalAlign: 'middle',
        '& > *': {
            margin: theme.spacing(2),
            width: '100%',
            /* height: theme.spacing(16), */
        },
    },
    grid: {
        padding: theme.spacing(1),
    }
}));

const mockCodes = [
    { code: 'KPNL145' },
    { code: 'KNUGOVNO' },
    { code: '145LOVE145' },
];

const mockCities = [
    { code: 'Rubezhnoe' },
    { code: 'Paris' },
    { code: 'London' },
];

function BrowseFlights() {
    const classes = useStyles();
    const [showFilters, setShowFilters] = useState(true);

    //TODO: fetch codes, cities

    return (<div>
        <div style={{ }}>
            Filters
            <IconButton
                color="primary"
                aria-label="show filters"
                size="small"
                onClick={() => setShowFilters(!showFilters)}
            >
                {showFilters ? <ArrowDropUpIcon/> : <ArrowDropDownIcon />}
            </IconButton>          
        </div>


        {!showFilters ? null :
            <Paper elevation={3} className={classes.paper}>               
                <Grid container spacing={5} alignItems="center" className={classes.grid}>
                    <Grid container spacing={5} alignItems="center" className={classes.grid}>
                        <Grid item>
                            Flight Code:
                        </Grid>
                        <Grid item>
                            <Autocomplete
                                className={classes.fileds}
                                size="small"
                                id="combo-box-code"
                                options={mockCodes}
                                getOptionLabel={(option) => option.code}
                                style={{ width: 300 }}
                                renderInput={(params) => <TextField {...params} placeholder="KPNL145" variant="outlined" />}
                            />
                        </Grid>
                    </Grid>

                    <Grid container spacing={5} alignItems="center" className={classes.grid}>
                        <Grid item>
                            Destination:
                        </Grid>
                        <Grid item>
                            <Autocomplete
                                className={classes.fileds}
                                size="small"
                                id="combo-box-destination"
                                options={mockCities}
                                getOptionLabel={(option) => option.code}
                                style={{ width: 300 }}
                                renderInput={(params) => <TextField {...params} placeholder="Rubezhnoe" variant="outlined" />}
                            />
                        </Grid>
                    </Grid>

                    <Grid container spacing={5} alignItems="center" className={classes.grid}>
                        <Grid item>
                            Date:
                        </Grid>
                        <Grid item>
                            <TextField
                                id="date"
                                type="date"
                                InputLabelProps={{
                                    shrink: true,
                                }}
                            />
                        </Grid>
                    </Grid>


                </Grid>
            </Paper>
        }

        <table>
            <tr>
                <td></td>
                <td>

                </td>
            </tr>
        </table>

    </div> );

}

export default BrowseFlights;
